import React, { Component } from 'react';

class BannerInfo extends Component {
  static defaultProps = {
    info: {
      name: "asd이asdasd름",
      phone: '010-0000-0000',
      id: 0
    }
  }
  
  render() {
    const style = {
      border: '1px solid black',
      padding: '8px',
      margin: '8px'
    };

    const {
      name, phone, id
    } = this.props.info;
    
    return (
      <div style={style}>
        <div><b>{name}</b><b>{id}</b></div>
        <div>{phone}</div>
      </div>
    );
  }
}

export default BannerInfo;
