import React,{useState} from "react";
import './head_foot.css';
import '../../App.css';

const Hamb =() => {
const [hamOpen, hmMenu] = useState(false);  // 메뉴의 초기값을 false로 설정
const togglehamMenu = () => {
    hmMenu(hamOpen => !hamOpen); // on,off 개념 boolean
}

        return(
            <>
                <input type="checkbox" id="hamb_menu" className="hamb_menu" onClick={()=>togglehamMenu()}/>
                <label htmlFor="hamb_menu">
                    <span></span>
                    <span></span>
                    <span></span>
                </label>
                <div className={hamOpen ? "hm_wrap_show" : "hm_wrap_hide"}>
                    <div className="hm_wrap">
                        <div className="rel_line"></div>
                    </div>
                </div>
            </>
        )
    
}

export default Hamb;
