import React, {useState}  from 'react'
import { default as useScrollFadeIn } from '../../hooks/useScrollFadeIn'
import Modal from '../include/Modal'
const LandingList = () => {
    const animatedItem = {
        0: useScrollFadeIn('up', 1, 0),
        1: useScrollFadeIn('up', 1, 0.2),
        2: useScrollFadeIn('up', 1, 0.3),
        3: useScrollFadeIn('up', 1, 0.4),
        4: useScrollFadeIn('up', 1, 0.5),
        5: useScrollFadeIn('up', 1, 0.6),
        6: useScrollFadeIn('up', 1, 0.7),
        7: useScrollFadeIn('up', 1, 0.8),
        8: useScrollFadeIn('up', 1, 0.9),
    };
    const menu = [
        {
            id:0,
            title:"네오클릭 이벤트",
            text:"닷네임코리아X네오클릭 이벤트"
        },
        {
            id:1,
            title:"최신인기영화 이벤트",
            text:"연장상품에 따른 영화관람권 증정"
        },
        {
            id:2,
            title:"도메인 이벤트",
            text:"3가지 도메인의 할인 이벤트"
        },
        {
            id:3,
            title:"네번째이미지",
            text:"ddd"
        },
        {
            id:4,
            title:"다섯번째이미지",
            text:"eee"
        },
        {
            id:5,
            title:"여섯번째이미지",
            text:"fff"
        },
        {
            id:6,
            title:"일곱번째이미지",
            text:"ggg"
        },
        {
            id:7,
            title:"여덟번째이미지",
            text:"hhh"
        },
        {
            id:8,
            title:"아홉번째이미지",
            text:"iii"
        }
    ]
    const [modalVisible, setModalVisible] = useState(false)
    
    const closeModal = () => {
        setModalVisible(false)
    }
    const srcItem = [
        {id:0},
        {id:1},
        {id:2},
    ]
    const srcList = srcItem.map((srcitems) => {
    const {id} = srcitems
    return(
        <div>{
            modalVisible && <Modal
            visible={modalVisible}
            closable={true}
            maskClosable={true}
            onClose={closeModal}>
                <img className="land_img" alt="랜딩페이지 썸네일" src={process.env.PUBLIC_URL + '/img/landing/' + id + '.jpg'}/>
            </Modal>}
        </div>
    )
    
    });
    const [content, setContent] = useState();
    const openModal = () => {
        setModalVisible(true)
    }
    const buttonValueSetting = e => {
        const { id  } = e.target;
        setContent( id );
    };
    
    const landList = menu.map((item)=>{
    const {id,title,text} = item
                return(
                    <div className="ld_cnt_wrap" >
                        <div className="ld_cnt"  {...animatedItem[id]}  onClick={buttonValueSetting}>
                            <img className="ld_img" alt="랜딩페이지 썸네일" id={id} src={process.env.PUBLIC_URL + '/img/landing/thumnail/' + id + '.jpg'}/>
                            <div className="ld_cnt_txt">
                                제목 : {title} <br/>
                                설명 : {text}
                            </div>
                        </div>
                    </div>
                )
            });
    console.log(content);
    return(
        <div className="cnt_inner_wrap2">
            {landList}
            {srcList[setContent]}
        </div>      
    )
    
}


export default LandingList;
