import React  from 'react'
import { default as useScrollFadeIn } from '../hooks/useScrollFadeIn'
    const animatedItem = {
        0: useScrollFadeIn('up', 1, 0),
        1: useScrollFadeIn('up', 1, 0.2),
        2: useScrollFadeIn('up', 1, 0.3),
        3: useScrollFadeIn('up', 1, 0.4),
        4: useScrollFadeIn('up', 1, 0.5),
        5: useScrollFadeIn('up', 1, 0.6),
        6: useScrollFadeIn('up', 1, 0.7),
        7: useScrollFadeIn('up', 1, 0.8),
        8: useScrollFadeIn('up', 1, 0.9),
      };
const LandingList = () => {
        const menu = [
            {
                id:0,
                title:"첫번째이미지",
                text:"aaa"
            },
            {
                id:1,
                title:"두번째이미지",
                text:"bbb"
            },
            {
                id:2,
                title:"세번째이미지",
                text:"ccc"
            },
            {
                id:3,
                title:"네번째이미지",
                text:"ddd"
            },
            {
                id:4,
                title:"다섯번째이미지",
                text:"eee"
            },
            {
                id:5,
                title:"여섯번째이미지",
                text:"fff"
            },
            {
                id:6,
                title:"일곱번째이미지",
                text:"ggg"
            },
            {
                id:7,
                title:"여덟번째이미지",
                text:"hhh"
            },
            {
                id:8,
                title:"아홉번째이미지",
                text:"iii"
            }
        ]
        const menuList2 = menu.map((item)=>{
        const {id,title,text} = item
                    return(
                        <div className="ld_cnt_wrap" >
                            <div className="ld_cnt" id={id} onClick={() => window.open(process.env.PUBLIC_URL + '/img/landing/' + id + '.jpg', '_blank')}>
                                <img className="ld_img" alt="랜딩페이지 썸네일" src={process.env.PUBLIC_URL + '/img/landing/thumnail/' + id + '.jpg'}/>
                                <div className="ld_cnt_txt">
                                    제목 : {title} <br/>
                                    설명 : {text}
                                </div>
                            </div>
                        </div>
                    )
                });
        return(
            <div className="cnt_inner_wrap2">
                {menuList2}
            </div>      
        )
    }


export default LandingList;
