import React, {Component} from 'react'
import './cnts/cnt.css';
import '../App.css';
import './pages.css';

class LandingPage extends Component {
    state = {
        menu: [
            {
                id:1,
                url:"/Blog",
                text:"Blog"
            },
            {
                id:2,
                url:"/Velog",
                text:"Velog"
            },
            {
                id:3,
                url:"/Github",
                text:"Github"
            }
        ]
    }
    render(){
        const menus = [
        "1", "2", "3", "4",
        "5", "6", "7", "8",
        "9"
    ]
        return(
            <div className="cnt_wrap">
            <div className="cnt">
                <div className="cnt_tit">랜딩 / 상세페이지 디자인</div>
                <div className="tit_line"></div>
                <div className="cnt_sub_tit">Page_Design / 작업물 중 일부를 첨부하였습니다.</div>
                <div className="cnt_sec cnt_sec2 land_sec">
                    <div className="cnt_inner_wrap2">
                            {
                                this.state.menu.map((item)=>{
                                    const {id,url,text} = item
                                    return(
                                        <div className="ld_cnt_wrap" >
                                            <div className="ld_cnt">
                                                <img className="ld_img" alt="랜딩페이지 썸네일" src={process.env.PUBLIC_URL + '/img/landing/thumnail/' + index + '.jpg'}/>
                                                <div className="ld_cnt_txt">
                                                    제목 : {id}{url}{text}<br/>
                                                    설명 : {
                                                    
                                                    ((process.env.PUBLIC_URL + '/img/landing/thumnail/' + index + '.jpg').split('/')[5].split('.')[0])
                                                    
                                                    }
                                                </div>
                                            </div>
                                        </div>
                                    )
                                })
                            }
                    </div>      
                </div>
            </div>
        </div>
        )
    }
}

export default LandingPage;
