import {React} from "react";
import './cnts/cnt.css';
import '../App.css';
import './pages.css';


const LandingPage =() => {
    const menus = [
        "1", "2", "3", "4",
        "5", "6", "7", "8",
        "9"
    ]
    const fs = require("fs");
    const _url = request.url;
    const fullUrl = new URL("http://localhost:3000" + _url);
    const pathName = fullUrl.pathname;
    if (pathName === "/") {
        fs.readdir(process.env.PUBLIC_URL + '/img/landing/', (err, files) => {
          console.log(files);
        });
      }
    ;
    const menuList = menus.map((menu, index) => (

        <div className="ld_cnt_wrap" key={index} id={menu} onClick={() => window.open(process.env.PUBLIC_URL + '/img/banner/' + index + '.jpg', '_blank', 'height=300px', 'width=760px')}>
            <div className="ld_cnt">
                <img className="ld_img" alt="배너사진" src={process.env.PUBLIC_URL + '/img/landing/thumnail/' + index + '.jpg'}/>
                <div className="ld_cnt_txt">
                    제목 : {menu}<br/>
                    설명 : {
                    
                    ((process.env.PUBLIC_URL + '/img/landing/thumnail/' + index + '.jpg').split('/')[5].split('.')[0])
                    
                    }
                </div>
            </div>
        </div>
    ));
    
    return(
        <div className="cnt_wrap">
            <div className="cnt">
                <div className="cnt_tit">랜딩 / 상세페이지 디자인</div>
                <div className="tit_line"></div>
                <div className="cnt_sub_tit">Page_Design / 작업물 중 일부를 첨부하였습니다.</div>
                <div className="cnt_sec cnt_sec2 land_sec">
                    <div className="cnt_inner_wrap2">    
                        {menuList}
                        <br/><br/><br/><br/><br/><br/> 
                    </div>      
                </div>
            </div>
        </div>
    )
    
}

export default LandingPage;
