import React from 'react';
import styled from 'styled-components';
import Spinner from '../img/loading.gif';

const Loading =() => {
    const Background = styled.div`
        position: absolute;
        width: 100vw;
        height: 100vh;
        top: 0;
        left: 0;
        background: #080f2a;
        z-index: 999;
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
    `;
    const BgInner = styled.div`
        position: relative;
        text-align: center;
        background: #fff;
        width: 250px;
        height: 250px;
        border-radius: 50%;
        box-shadow: inset 0px 0px 20px 3px rgba(0,0,0,0.6);
    `;
    return (
            <Background>
                <BgInner>
                    <img className="spinner" src={Spinner} alt="로딩중" width="150px" />
                </BgInner>  
            </Background>
            );
        };
        
export default Loading;
