import React, {Component, useState} from 'react'
import './cnts/cnt.css';
import '../App.css';
import './pages.css';

class LandingPage extends Component {
    state = {
        menu: [
            {
                id:0,
                title:"첫번째이미지",
                text:"aaa"
            },
            {
                id:1,
                title:"두번째이미지",
                text:"bbb"
            },
            {
                id:2,
                title:"세번째이미지",
                text:"ccc"
            },
            {
                id:3,
                title:"네번째이미지",
                text:"ddd"
            },
            {
                id:4,
                title:"다섯번째이미지",
                text:"eee"
            },
            {
                id:5,
                title:"여섯번째이미지",
                text:"fff"
            },
            {
                id:6,
                title:"일곱번째이미지",
                text:"ggg"
            },
            {
                id:7,
                title:"여덟번째이미지",
                text:"hhh"
            },
            {
                id:8,
                title:"아홉번째이미지",
                text:"iii"
            }
        ]
    }
    render(){
        const [modalOpen, setModalOpen] = useState(false);

        const openModal = () => {
            setModalOpen(true);
        };
        const closeModal = () => {
            setModalOpen(false);
        };
        return(
            <div className="cnt_wrap">
                <Modal open={modalOpen} close={closeModal} header="Modal heading">
                    // Modal.js <main> {props.children} </main>에 내용이 입력된다. 리액트 함수형 모달
                    팝업창입니다. 쉽게 만들 수 있어요. 같이 만들어봐요!
                </Modal>
            <div className="cnt">
                <div className="cnt_tit">랜딩 / 상세페이지 디자인</div>
                <div className="tit_line"></div>
                <div className="cnt_sub_tit">Page_Design / 작업물 중 일부를 첨부하였습니다.</div>
                <div className="cnt_sec cnt_sec2 land_sec">
                    <div className="cnt_inner_wrap2">
                            {
                                this.state.menu.map((item)=>{
                                    const {id,title,text} = item
                                    return(
                                        <div className="ld_cnt_wrap" >
                                            <div className="ld_cnt" id={id} onClick={openModal}>
                                                <img className="ld_img" alt="랜딩페이지 썸네일" src={process.env.PUBLIC_URL + '/img/landing/thumnail/' + id + '.jpg'}/>
                                                <div className="ld_cnt_txt">
                                                    제목 : {title} <br/>
                                                    설명 : {text}
                                                </div>
                                            </div>
                                        </div>
                                    )
                                })
                            }
                    </div>      
                </div>
            </div>
        </div>
        )
    }
}

export default LandingPage;
